package udemyProje;

public class MantiksalOperatorler {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * &&   and opereatoru  butun sonuclar dogru ise true  ama en az biri false ile sonuc false 
		 * 
		 * || or  operatoru  en biri true ise sonuc true cikar
		 * 
		 * 
		 * !---> not operatoru 
		 * 
		 *
		 */
		
		
		System.out.println(3==3);
		
		System.out.println(2<3);
		
		System.out.println("ali"=="ali");
		
		
		System.out.println(3==3 && 2<3 && "can"=="can");
		
		
		
		System.out.println(3==4 || 2<3 || "cana"=="can");
		
		System.out.println(!(3<4));
		
		System.out.println(3<4);
		
		
		
	}

}
